    const card: LiveDocCard = {
      destination,
      data: null,
      headers,
      // url: ;
      strategy: (deltaHandlingStrategy && deltaHandlingStrategy.label) || '',
      updates: 0,
      lastData: null,
      created: new Date(),
      terminated: null,
      subscription: null,
      state: 'Connecting',
    };

    const stream = this.liveDocService.getStream({
      document: destination,
      headers,
      deltaHandlingStrategy:
        deltaHandlingStrategy && deltaHandlingStrategy.function,
    });

    const subscription = stream.pipe(takeUntil(this.unsubscribe$)).subscribe({
      next: (data) => {
        this.unsubscribe$.next();
        this.unsubscribe$.complete();

        //console.log("Data Received",JSON.stringify(data, null, 2));

        switch (this.eventType) {
          case EVENT_TYPE.HOME_FEED:
            break;

          case EVENT_TYPE.EVENT_FEED:
            //console.log("Data Count")
            this.mainEventData = JSON.parse(JSON.stringify(data, null, 2));
            //console.log("Live Data", this.mainEventData);
            const raw = this.mainEventData;

            console.log('testsets >>...', raw?.participants[0][0]);
            this.homeTeam = this.mainEventData.participants[0][0]?.name;
            this.awayTeam = this.mainEventData.participants[0][1]?.name;
            this.gameTime = this.mainEventData.anticipated.startTime
              .split('T')[1]
              .substring(0, 5);
            this.marketId = this.mainEventData.miniCoupons['TOT1545'][0];
            this.gameDate =
              this.mainEventData.anticipated.startTime.toLocaleDateString(
                'en-GB',
                {
                  day: '2-digit',
                  month: '2-digit',
                }
              );

            console.log('game date >>', this.gameDate);

            this.subscribeOddDestinations(
              `${this.marketsFeedLink}${this.marketId}`,
              null,
              this.headers,
              false
            );

            break;
        }
      },
      error: (e) => {
        card.terminated = new Date();
        card.state = 'Error';
        //this.rippleCard(card, 'rgba(255, 0, 0, 0.2)');
        ++this.staleCardCount;
      },
      complete: () => {
        card.terminated = new Date();
        card.state = 'Unsubscribed';
        ++this.staleCardCount;
      },
    });

    card.subscription = subscription;

    this.liveDocCards.unshift(card);
  }

  loadEvent(eventData: EventData) {
    this.homeTeam = eventData.participants[0][0]?.name;
    this.awayTeam = eventData.participants[0][1]?.name;
    this.gameTime = eventData.anticipated.startTime
      .split('T')[1]
      .substring(0, 5);
    this.marketId = eventData.miniCoupons['MAIN'][0];

    this.bettingService
      .getMarkets(`${this.marketsFeedLink}${this.marketId}`)
      .pipe(takeUntil(this.unsubscribeAll))
      .subscribe({
        next: (data) => {
          console.log('Odd Data Received', JSON.stringify(data, null, 2));

          this.oddsEventData = JSON.parse(JSON.stringify(data, null, 2));

          console.log('debug Data', this.oddsEventData.selections[0][0]);

          this.homeWinOdd = this.oddsEventData.selections[0][0].price.dec;
          this.teamDrawOdd = this.oddsEventData.selections[0][1].price.dec;
          this.awayWinOdd = this.oddsEventData.selections[0][2].price.dec;

          this.oddsEventData.selections[0].forEach((data) => {
            switch (this.marketType) {
              case MARKET_TYPE.MAIN:
                data.marketType = '1X2';
                break;

              case MARKET_TYPE.OVER25:
                data.marketType = 'ov2.5';
                break;
            }

            data.eventId = eventData.id;
            data.eventName = this.homeTeam + ' vs ' + this.awayTeam;
            this.selections.push(data);
          });

          this.checkItemsBetSlip();
        },
        error: (e) => {},
        complete: () => {},
      });

    //this.subscribeOddDestinations(`${this.marketsFeedLink}${this.marketId}`, null, this.headers, false);
  }

  loadEventDemo(eventData: EventData) {
    this.homeTeam = eventData.participants[0][0]?.name;
    this.awayTeam = eventData.participants[0][1]?.name;
    this.gameTime = eventData.anticipated.startTime
      .split('T')[1]
      .substring(0, 5);
    this.marketId = eventData.miniCoupons['MAIN'][0];

    this.demoBettingService
      .getMarkets()
      .pipe()
      .subscribe({
        next: (data) => {
          console.log('Odd Data Received', JSON.stringify(data, null, 2));

          this.oddsEventData = JSON.parse(JSON.stringify(data, null, 2));

          console.log('debug Data', this.oddsEventData.selections[0][0]);

          this.homeWinOdd = this.oddsEventData.selections[0][0].price.dec;
          this.teamDrawOdd = this.oddsEventData.selections[0][1].price.dec;
          this.awayWinOdd = this.oddsEventData.selections[0][2].price.dec;
          this.oddsEventData.selections[0].forEach((data) => {
            switch (this.marketType) {
              case MARKET_TYPE.MAIN:
                data.marketType = '1X2';
                break;

              case MARKET_TYPE.OVER25:
                data.marketType = 'ov2.5';
                break;
            }

            data.eventId = eventData.id;
            data.eventName = this.homeTeam + ' vs ' + this.awayTeam;
            this.selections.push(data);
            
          });
          this.checkItemsBetSlip();
          //this.selections.push(...this.oddsEventData.selections[0]);
        },
        error: (e) => {},
        complete: () => {},
      });

    //this.subscribeOddDestinations(`${this.marketsFeedLink}${this.marketId}`, null, this.headers, false);
  }

  newOddSubscription(
    destination: string,
    deltaHandlingStrategy?: DeltaMergeStrategy,
    headers?: StompHeaders
  ) {
    console.group('New Subscription Parameters:');
    console.table({
      destination,
      headers:
        Object.entries(headers || {})
          .map(([key, value]) => key + ' : ' + value)
          .join(';   ') || null,
      deltaMerge: deltaHandlingStrategy && deltaHandlingStrategy.label,
    });
    console.groupEnd();

    const card: LiveDocCard = {
      destination,
      data: null,
      headers,
      // url: ;
      strategy: (deltaHandlingStrategy && deltaHandlingStrategy.label) || '',
      updates: 0,
      lastData: null,
      created: new Date(),
      terminated: null,
      subscription: null,
      state: 'Connecting',
    };

    const stream = this.liveDocService.getStream({
      document: destination,
      headers,
      deltaHandlingStrategy:
        deltaHandlingStrategy && deltaHandlingStrategy.function,
    });

    const subscription = stream.pipe(takeUntil(this.unsubscribeAll)).subscribe({
      next: (data) => {
        console.log('Odd Data Received', JSON.stringify(data, null, 2));

        this.oddsEventData = JSON.parse(JSON.stringify(data, null, 2));

        console.log('debug Data', this.oddsEventData.selections[0][0]);

        this.homeWinOdd = this.oddsEventData.selections[0][0].price.dec;
        this.teamDrawOdd = this.oddsEventData.selections[0][1].price.dec;
        this.awayWinOdd = this.oddsEventData.selections[0][2].price.dec;
      },
      error: (e) => {
        card.terminated = new Date();
        card.state = 'Error';
        //this.rippleCard(card, 'rgba(255, 0, 0, 0.2)');
        ++this.staleCardCount;
      },
      complete: () => {
        card.terminated = new Date();
        card.state = 'Unsubscribed';
        ++this.staleCardCount;
      },
    });

    card.subscription = subscription;

    this.liveDocCards.unshift(card);
  }

  loadHomeEvents(data: EventData) {
    this.eventType = EVENT_TYPE.EVENT_FEED;
    this.subscribeAllDestinations(
      `${this.eventsFeedLink}${data.id}`,
      null,
      this.headers,
      false
    );
  }

  unsubscribeCard(card: LiveDocCard) {
    card.subscription.unsubscribe();
    this.staleCardCount++;
    card.state = 'Unsubscribed';
  }

  removeCard(card: LiveDocCard) {
    // if ( card.state === 'Error' || card.state === 'Unsubscribed' ) {
    --this.staleCardCount; // Only stale can be removed
    this.liveDocCards = this.liveDocCards.filter(
      (candidate) => candidate !== card
    );
  }

  removeAll() {
    this.unsubscribeAll.next();
    this.liveDocCards = [];
    this.staleCardCount = 0;
  }

  removeStale() {
    this.liveDocCards = this.liveDocCards.filter(
      (card) => card.state === 'Connecting' || card.state === 'Connected'
    );
    this.staleCardCount = 0;
  }

  // send() {
  //   this.dialog.open<StompSenderComponent, StompSenderIO, StompSenderIO>(StompSenderComponent, {
  //     data: {
