        next: (data) => {
          this.oddsEventData = JSON.parse(JSON.stringify(data, null, 2));
          this.homeWinOdd = this.oddsEventData.selections[0][0].price.dec;
          this.teamDrawOdd = this.oddsEventData.selections[0][1].price.dec;
          this.awayWinOdd = this.oddsEventData.selections[0][2].price.dec;

          const betslipData = localStorage.getItem('zebet_betslip_key_dashboard');
          const convertedBetslipData = JSON.parse(betslipData).data;

          this.oddsEventData.selections[0].forEach((data) => {
            switch (this.marketType) {
              case MARKET_TYPE.MAIN:
                data.marketType = '1X2';
                break;

              case MARKET_TYPE.OVER25:
                data.marketType = 'ov2.5';
                break;
            }

            data.eventId = eventData.id;
            data.eventName = this.homeTeam + ' vs ' + this.awayTeam;

            for (const item of convertedBetslipData) {
              if (item.eventId == eventData.id) {
                if (item.side == data.side) {
                  data.isSelected = true;
                }
              }
            }

            this.selections.push(data);
          });
        },
        error: (e) => { },
        complete: () => { },
      });

    //this.subscribeOddDestinations(`${this.marketsFeedLink}${this.marketId}`, null, this.headers, false);
  }

