    if (width >= 0 && width <= 575) sizes = SCREEN_SIZE.XS;
    else if (width >= 576 && width <= 767) sizes = SCREEN_SIZE.SM;
    else if (width >= 768 && width <= 1023) sizes = SCREEN_SIZE.MD;
    else if (width >= 1024 && width <= 1279) sizes = SCREEN_SIZE.LG;
    else if (width >= 1280) sizes = SCREEN_SIZE.XL;

    return sizes;
  }

}

