  public subscribeToUpcomingGames(): void {
    this.bettingService
      .upcomingGames(this.homeFeedLink)
      .pipe(
        take(5),
        pluck('events'),
        // takeUntil(this.unsubscribe$),
        //take(5),
        map((events) => Object.values(events)),
        switchMap((events: any[]) =>
          combineLatest(
            events.map((event: any) => {
              return (
                this.bettingService.getEvents(`${this.eventsFeedLink}${event.id}`)
                // this.bettingService.getCompetetion(`${this.competitionsLink}${event.competitionId}`)
              ) 
            })
            //   events.map((event: any) =>
            //     this.bettingService.getEvents(`${this.eventsFeedLink}${event.id}`),
            //     // this.bettingService.getCompetetion(`${this.competitionsLink}${event.competitionId}`)
            //   )
          ).pipe(takeUntil(this.unsubscribe$))
        )
      )
      .subscribe((data) => {
        console.log('upcoming games', data);
        if (data.length === 0) {
          console.log('No data found for upcoming games.');
          return;
        }
        // console.log('Home Data', data);
        // console.log('Home Data lenght', data.length);
        this.isLoaded = true;

        this.eventFeed.length = 0;
        this.eventFeed.push(...selectArray(data));

        this.unsubscribe$.next();
        this.unsubscribe$.complete();
        //});
      });
  }




