      default: { 
         //statements; 
         w = 'w-4';
         h = 'h-4';
         p = "p-1.5";
         break; 
      } 
   } 

    return [ w, h, p, mode];
  }
  public get classesText(): string[] {
    const mode = this.primary ? 'text-gray-900' : 'text-white';
    return [ mode];
  }
  public get classesB3A(): string[] {
    const mode = this.primary ?  'stroke-gray-900' : 'stroke-white';
    let w = 'w-3';
    let h = "h-3";
    switch(this.size) { 
      case 'small': { 
         //statements; 
         w = 'w-2';
         h = 'h-2';
         break; 
      } 
      case 'medium': { 
         //statements; 
         w = 'w-3';
         h = 'h-3';
         break; 
      } 
      case 'large': { 
        //statements; 
        w = 'w-4';
        h = 'h-4';
       break; 
     } 
      default: { 
         //statements; 
         w = 'w-3';
         h = 'h-3';
         break; 
      } 
   } 
    return [ w, h, mode];
  }
}

import { Component, HostListener } from '@angular/core';
import { MatSlideToggleModule } from '@angular/material/slide-toggle';

@Component({
  selector: 'app-bet-history',
  templateUrl: './bet-history.component.html',
  styleUrls: ['./bet-history.component.css'],

  imports: [MatSlideToggleModule],
})
export class BetHistoryComponent {
  calendarDates: number[] = [];
  selectedDate: Date;
  formattedDate: string;
  showFilterModal = false;
  showOptions = false;
  showOptions2 = false;
  showNumberOptions = false;
  showNumberOptions2 = false;
  isOpen = false;
  mobileOpen = false;
  tabOpen = false;

  constructor() {
    for (let i = 1; i <= 30; i++) {
      this.calendarDates.push(i);
    }
    this.selectDate(25);
  }

  selectDate(date) {
    this.selectedDate = new Date(2023, 8, date);
    const day = this.selectedDate.getDate();
    const month = this.selectedDate.toLocaleString('default', {
      month: 'short',
    });
    const year = this.selectedDate.getFullYear();

    this.formattedDate = `${day}th ${month} ${year}`;
   
  }

  isDateActive(date: number) {
    if (this.selectedDate) {
      const selected = this.selectedDate.getDate();
      return date === selected;
    }
    return false;
  }

  @HostListener('window:resize', []) updateDays() {
    if (window.innerWidth <= 800) {
      this.tabOpen = false;
    }
  }
}

