import { Component, EventEmitter, OnInit, Output, inject } from '@angular/core';
import { BettingService } from '../../services/betting-service';
import { Selection } from '../../dto/odd-data.dto';
import { BETSLIP_TAB } from '../../util/enum';
import { FormArray, FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { LocalStorageService } from '../../util/local-storage.service';
import { MatDialog } from '@angular/material/dialog';
import { BookABetDesktopComponent } from '../../shared/book-a-bet-desktop/book-a-bet-desktop.component';
import { BookBetslipDesktopComponent } from '../../shared/book-betslip-desktop/book-betslip-desktop.component';
import html2canvas from 'html2canvas';
import { environment } from 'projects/sportsbook/src/environments/environment';
import { HttpClient } from '@angular/common/http';
import('preline')
import { LoaderService } from '../../services/loader.service';

@Component({
  selector: 'app-bet-slip-desktop',
  templateUrl: './bet-slip-desktop.component.html',
  styleUrls: ['./bet-slip-desktop.component.css']
})
export class BetSlipDesktopComponent implements OnInit {
  @Output() hideBetSlip = new EventEmitter();
  tab: string = 'Single';
  betslipTab: BETSLIP_TAB = BETSLIP_TAB.SINGLE;
  betSlip: Selection[] = [];
  betSlipLoader: Selection[] = [];
  totalOdds: number = 0;
  singleBetslipForm: FormGroup;
  firstTab: string = 'Open Bets(0)';
  _localStorageService = inject(LocalStorageService);
  isLoggedIn: boolean = false;
  betShow: boolean = true;
  stakeAmount = 0;
  stakeAmountSingle = 0;
  stakeAmountCombi: number[] = [];
  potentialWinning: any = 0;
  thirdTab: string = 'Betslip';
  allowOddChanges: boolean = false;
  oddChangePlaceBet: boolean = false;
  oddShowMsg: boolean = false;
  authWait: boolean = false;
  isModalVisible: boolean = false;
  betSlipData: any;
  betSlipCode: string = '';
  isFullScreen: boolean = false;
  optionsSet: string[] = [];
  categorizedCombinations: { label: string; combinations: string[][] }[] = [];
  totalStakeAmount: number = 0;
  multipliedValues: number[] = [];
  totalNumberEvents: number = 0;


  constructor(private bettingService: BettingService, private fb:FormBuilder, private dialog: MatDialog ,     private httpClient: HttpClient,
    private localStorageService: LocalStorageService,
    private loaderService: LoaderService,)  {
    this.singleBetslipForm = this.fb.group({
      betslipItems: this.fb.array([]),
    });
  }

  isVisible: boolean = false;
  toggleVisibility() {
    this.isVisible = !this.isVisible;
  }

  onBetslipClick() {
    this.loaderService.toggleVisibility();
  }



  checkAllowOddChanges() {
    console.log('allowOddChanges', this.allowOddChanges);

    if (this.allowOddChanges) {

    } else {
      console.log('Odds not checked.');
    }
  }

  ngOnInit(): void {
    let betSlipLoader: Selection[] = [];
    this.bettingService.betbasket.subscribe((value) => {
      betSlipLoader = value;
      this.calculateTotalOdds(betSlipLoader);
      this.betSlip = betSlipLoader;
      this.generateCategorizedCombinations();
      this.createFormControls();
    });
    this.betSlip = betSlipLoader;
    console.log("Betslip Data", this.betSlip.length);
    if (localStorage.getItem('accessToken')) {
      this.isLoggedIn = true;
    } else {
      this.isLoggedIn = false;
    }
  }

  createFormControls() {
    const formArray = this.singleBetslipForm.get('betslipItems') as FormArray;
    formArray.clear();

    for (let i = 0; i < this.betSlip.length; i++) {
      formArray.push(this.fb.control(''));
    }
  }

  callBetslipApi() {
    let betSlipLoader: Selection[] = [];
    this.bettingService.betbasket.subscribe((value) => {
      betSlipLoader = value;
      console.log("Betslip Data", betSlipLoader);
      this.calculateTotalOdds(betSlipLoader);
      this.betSlip = betSlipLoader;
    });
    console.log(betSlipLoader);
    let createBetSlip = {
      "matchItems": { betSlipLoader }
    };

    console.log("Betslip Data", this.betSlip.length);
    if (localStorage.getItem('accessToken')) {
      this.isLoggedIn = true;
    } else {
      this.isLoggedIn = false;
    }
    console.log('login Status', this.isLoggedIn)
    try {
      const betSlipObject = createBetSlip;
      const url = `${environment.BetSlipUrl}/booking`;
      fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(betSlipObject) // Assuming betSlipLoader is the payload
      })
        .then(response => {
          console.log('Response status:', response.status);
          if (response.status === 200 || response.status === 201) {
            this.isModalVisible = true;
            response.json().then(data => {
              this.betSlipCode = data.data.code
              console.log('Response data:', this.betSlipCode);
              this.getBetSlipData(this.betSlipCode);
              // textInput.value = this.betSlipCode;
            });

          } else {
            this.isModalVisible = false;
          }

          return response.json();
        });
    } catch (error) {
      console.error('Error:', error);
    }

  }
  closeModal() {
    this.isModalVisible = false;
  }

  getBetSlipData(betSlipCode: any) {

    const apiUrl = `${environment.BetSlipUrl}?betSlipCode=${betSlipCode}`;

    this.httpClient.get(apiUrl).subscribe(
      (response: any) => {
        this.betSlipData = response;
        console.log('Bet Slip Data:', this.betSlipData);
        // Handle the bet slip data as needed
      },
      (error) => {
        console.error('Error fetching bet slip data:', error);
        // Handle the error
      }
    );
  }

  get betslips(): FormArray {
    return this.singleBetslipForm.get("betslipItems") as FormArray;
  }

  onThirdTabClick(tab: string) {
    this.thirdTab = tab;
    if (this.thirdTab === 'Betslip') {
      this.betShow = true;
    }
    if (this.thirdTab === 'Cashout') {
      this.betShow = false;
    }
  }

  placeBet() {
    this.authWait = true;
    setTimeout(() => {
      this.authWait = false;
      this.oddShowMsg = true;
      if (this.allowOddChanges) {
        this.oddChangePlaceBet = true;
      } else {
        this.oddChangePlaceBet = false;
      }
    }, 2000);
  }

  marketReload() {
    this.oddShowMsg = false;
  }

  closeBetMsg() {
    this.oddShowMsg = false;
  }

  populateGames() {
    let game = {};

    if (this.betslips.length == 0) {
      //this.betslips.clear();

      this.betSlip.forEach(element => {
        game = new FormControl('');
        this.betslips.push(game);
      });
    }


    console.log(this.betslips.value);
  }


  onFirstTabClick(tab: string) {
    this.firstTab = tab;
  }

  onKeyUp(x) { // appending the updated value to the variable
    console.log(x.target.value);
    if (x.target.value)
      this.stakeAmount = parseInt(x.target.value);
    else
      this.stakeAmount = 0;
    this.calculatePotentialWinning();
  }


  onKeyUpCombi(event: any, index: number) {
    const individualStakeAmount = +event.target.value;
    if (!isNaN(individualStakeAmount)) {
      const multipliedValue = individualStakeAmount * this.categorizedCombinations[index].combinations.length;
      this.multipliedValues[index] = multipliedValue;
      this.stakeAmount = this.calculateTotalStakeAmount();
    }
    this.calculatePotentialWinningSingle(this.stakeAmount, this.totalOdds);
  }

  calculateTotalStakeAmount() {
    return this.multipliedValues.reduce((total, value) => total + value, 0);
  }

  private inputValues: { value: number, length: number }[] = [];

  onKeyUpSingle(x, i: number) {
    this.potentialWinning = 0;
    this.stakeAmount = 0;
    this.stakeAmountSingle = 0;
    if (x.target.value) {
      this.stakeAmountSingle = parseInt(x.target.value);
      if (this.inputValues[i]) {
        this.inputValues[i].value = this.stakeAmountSingle;
      } else {
        this.inputValues[i] = { value: this.stakeAmountSingle, length: i };
      }
    } else {
      this.inputValues = this.inputValues.filter(item => item.length !== i);
    }
    this.inputValues.forEach((input) => {
      this.stakeAmount += input.value;
    });
    this.totalOdds = this.betSlip.reduce((acc, odds) => acc + Number(odds.price.dec), 0);
    this.potentialWinning = 0;
    this.calculatePotentialWinningSingle(this.stakeAmount, this.totalOdds);
  }

  // removeGame(index: number) {
  //this.betslips.removeAt(index);
  // console.log('betslips.value', this.betslips.value);
  // console.log('betslips index', index);
  // this.betslips.removeAt(index);
  // this.bettingService.deleteToBetSlip(this.betSlip[index]);


  // this.potentialWinning = 0;
  // this.stakeAmount = 0;
  // this.betslips.controls.forEach((element, index) => {
  //   console.log("Index",  this.betSlip[index])
  //   if(element.value)
  //   {
  //     this.stakeAmountSingle = parseInt(element.value);
  //     this.stakeAmount += this.stakeAmountSingle;
  //   } 
  //   else 
  //     this.stakeAmountSingle = 0;

  //     this.calculatePotentialWinningSingle(this.stakeAmountSingle, this.betSlip[index].price.dec);
  // });
  // }
  // removeGame(index: number) {
  //   console.log("Removing item at index:", index);
  //   if (index >= 0) {
  //     this.betslips.removeAt(index);
  //     this.bettingService.deleteToBetSlip(this.betSlip[index]);
  //     // Add console.log statements to check the state of your data.
  //   }
  // }

  removeGame(index: number) {
    const betToRemove = this.betSlip[index];
    console.log("Item ID:", betToRemove.id);
    console.log("Bet ID:", this.betSlip[index].id);
    this.bettingService.deleteToBetSlip(betToRemove);
    this.generateCategorizedCombinations();
  }

  calculateTotalOdds(betslip: Selection[]) {
    let subTotal = 0;
    betslip.forEach(data => {
      subTotal += parseFloat(data.price.dec);
    })
    this.totalOdds = parseFloat(subTotal.toFixed(2));
  }

  display() {
    this.hideBetSlip.emit()
  }

  onTabClick(tab: string) {
    this.tab = tab;
    this.potentialWinning = 0;
    this.stakeAmount = 0;
    if (tab == "Single") {
      this.populateGames();
      console.log("Potential Winnings", this.potentialWinning);
    }
    else if (tab == "ACCA")
      this.calculatePotentialWinning();
  }

  setStakeAmount(amount: number) {
    this.stakeAmount += amount;
    this.calculatePotentialWinning();
  }

  clearBetSlip() {
    this.bettingService.clearBetSlip();
    this.totalOdds = 0;
    this.stakeAmount = 0;
    this.calculatePotentialWinning();
  }

  calculatePotentialWinning() {
    this.potentialWinning = (this.stakeAmount * this.totalOdds).toFixed(2);
  }

  calculatePotentialWinningSingle(amount: number, odds: number): void {
    let potentialWinning: number = odds * amount;
    this.potentialWinning = potentialWinning.toFixed(2);
  }


  copyText(value: string) {
    this.copyToClipboard(value);
  }

  copyToClipboard(value: string) {
    const el = document.createElement('textarea');
    el.value = value;
    document.body.appendChild(el);
    el.select();
    document.execCommand('copy');
    document.body.removeChild(el);
    alert('Text copied to clipboard: ' + value);
  }
  downloadDivContent() {
    const element = document.querySelector('.bet-slip-div') as HTMLElement;
    html2canvas(element).then((canvas) => {
      // Convert the canvas to a data URL and download it
      const link = document.createElement('a');
      link.download = 'betSlip.png';
      link.href = canvas.toDataURL();
      link.click();
    });
  }
  sharePage(): void {
    const pageUrl = window.location.href;
    if (navigator.share) {
      navigator.share({
        title: 'Check out this page!',
        url: pageUrl
      })
        .then(() => console.log('Shared successfully'))
        .catch(err => console.error('Error sharing:', err));
    } else {
      const shareOptions = {
        text: 'Check out this page: ',
        url: pageUrl
      };
      console.log('Share the page: ', shareOptions);
    }
  }


  generateCategorizedCombinations() {
    this.categorizedCombinations = [];
    const eventNameCountMap = new Map<string, number>();
    const eventNamesArray: string[] = []; // New array to store event names

    this.betSlip.forEach(item => {
      const eventName = item.eventName;
      eventNameCountMap.set(eventName, (eventNameCountMap.get(eventName) || 0) + 1);
      eventNamesArray.push(eventName); // Add event name to the new array
    });

    if (this.betSlip.length > 0) {
      this.optionsSet = Array.from({ length: this.betSlip.length }, (_, index) => String.fromCharCode(65 + index));
      console.log('this.betSlip', this.betSlip.length);
    }

    for (let r = 1; r <= this.optionsSet.length; r++) {
      const combinations = this.getCombinations(this.optionsSet, r);
      this.categorizedCombinations.push({ label: `${r} Fold`, combinations });

      // Log events for each combination
      combinations.forEach(combination => {
        console.log(`Events for ${r} Fold: `, combination.map(index => eventNamesArray[Number(index)]));
      });
    }

    const matchesCounts = Array.from(eventNameCountMap.values());

    eventNameCountMap.forEach((count, eventName) => {
      console.log(`${eventName} count: ${count}`);
    });

    this.totalNumberEvents = matchesCounts.length;
    console.log('Counts of matches:', matchesCounts);

    // Log the new array of event names
    console.log('Event Names Array:', eventNamesArray);
  }


  getCombinations(options: string[], r: number): string[][] {
    const combinations: string[][] = [];
    const helper = (current: string[], start: number) => {
      if (current.length === r) {
        combinations.push([...current]);
        return;
      }
      for (let i = start; i < options.length; i++) {
        current.push(options[i]);
        helper(current, i + 1);
        current.pop();
      }
    };
    helper([], 0);
    return combinations;
  }

  toggleFullScreen() {
    this.isFullScreen = !this.isFullScreen;
  }

  bookABet() {
    const dialogRef = this.dialog.open(BookABetDesktopComponent, {
      height: 'auto',
      width: "50%",
      panelClass: 'book-a-bet-dialog',
      disableClose: true
    }) 
  }

  bookBetslip(){
    const dialogRef = this.dialog.open(BookBetslipDesktopComponent, {
      height: 'auto',
      width: "665px",
      panelClass: 'book-a-bet-dialog',
      disableClose: true
    }) 
  }
}

