import {Component, OnInit, AfterViewInit ,NO_ERRORS_SCHEMA, HostListener} from '@angular/core';
import {CommonModule } from '@angular/common'; 
import {MatButtonModule} from '@angular/material/button';
import {MatCardModule} from '@angular/material/card';
import { BlogService } from './blog.service';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';
// import Swiper from "swiper";
import { NavigationExtras, Router } from '@angular/router';
import { OwlOptions } from 'ngx-owl-carousel-o';
import { DataService } from './setblog';
import { environment } from '../../../environments/environment';


@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css'],
  standalone: true,
  schemas: [NO_ERRORS_SCHEMA],
  imports: [MatCardModule, MatButtonModule, CommonModule],
})

export class BlogComponent implements OnInit {
  storedData = localStorage.getItem('accessToken');
  name = "Angular";
  blogData: any; 
  sanitizedHtml!: SafeHtml; 
  currentPage = 1;
  itemsPerPage = 9;
  totalItems = 0;
  blog: any;
  categories: any;
  categoryId:string = '';
  activeCategory: string = 'All';
  ASSETS_URL = environment.ASSETS_URL;

  constructor(private blogService: BlogService, private router: Router, private dataService : DataService) {}

  ngOnInit(): void {
    this.loadBlogItems(this.currentPage, this.itemsPerPage, this.activeCategory);
    this.blogService.getItems()
    .subscribe((data: any) => {
      this.categories = data.data;
      console.log("blog_category",this.categories)
    });
  }


  loadBlogItems(page: number, limit: number, category: string) {
    this.activeCategory = category; // Update the active category
    if (category === 'All') {
      this.blogService.getAllBlogItems(page, limit).subscribe(
        (response: any) => {
          this.blogData = response.data;
          console.log("blogData", this.blogData);

          this.totalItems = response.meta.total_count;
        },
        (error) => {
          console.error('Error fetching all blogs:', error);
        }
      );
    } else {
      this.blogService.getBlogItems(page, limit, category).subscribe(
        (response: any) => {
          this.blogData = response.data;
          console.log("blogData", this.blogData);

          this.totalItems = response.meta.total_count;
        },
        (error) => {
          console.error('Error fetching blogs by category:', error);
        }
      );
    }
  }

  showAllBlogItems() {
    this.loadBlogItems(this.currentPage, this.itemsPerPage, 'All');
  }

  onCategoryClick(categoryId: string) {
    this.currentPage = 1; // Reset to the first page when a new category is selected
    this.activeCategory = categoryId; // Update the active category to the clicked category ID
    this.loadBlogItems(this.currentPage, this.itemsPerPage, categoryId);
  }
  onPageChange(newPage: number) {
    this.currentPage = newPage;
    this.loadBlogItems(this.currentPage, this.itemsPerPage,  this.categoryId);
  }

  calculateTotalPages(): number {
    return Math.ceil(this.totalItems / this.itemsPerPage);
  }


  // navigateToBlogPost(item: any) {
  //   this.dataService.setItemData(item);
  //   this.router.navigate(['/blog-content', item.id]);
  // }
  replaceSpacesWithHyphens(input: string): string {
    if (!input) {
      return '';
    }
    return input.replace(/\s+/g, '-');
  }
  
  navigateToBlogPost(item: any) {
    const queryParams = {
      banner: item.banner,
      main_content: item.main_content,
      button_url:item.button_url
    };
  
    this.router.navigate(['/blog-content', this.replaceSpacesWithHyphens(item.title)], {
        queryParams: queryParams
    });
    // this.router.navigate(['/blog-content', this.replaceSpacesWithHyphens(item.title)], navigationExtras);
  }


  customBlogOptions: OwlOptions = {
    loop: true,
    mouseDrag: true,
    touchDrag: true,
    margin: 20,
    pullDrag: false,
    autoplay: true,
    dots: false,
    autoplayTimeout: 5000,
    navSpeed: 700,
    navText: ['<img src="assets/img/arrow-left.png">', '<img src="assets/img/arrow-right.png">'],
    responsive: {
      0: {
        items: 1.5
      },
      // 475: {
      //   items: 2.5
      // },
      // 576: {
      //   items: 3.5
      // },
      // 768: {
      //   items: 4
      // },
      // 991: {
      //   items: 5
      // }
    },
    nav: true
  };

  // Add a property to determine if pagination should be shown
  showPagination = true;

  @HostListener('window:resize', ['$event'])
  onResize(event: any) {
    // Adjust showPagination based on the window width
    if (window.innerWidth < 768) {
      this.showPagination = false;
    } else {
      this.showPagination = true;
    }
  }

}

import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { BlogService } from '../blog.service';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';
import { Router } from '@angular/router';
import { DataService } from '../setblog';
import { environment } from '../../../../environments/environment';


@Component({
  selector: 'app-blog-post',
  templateUrl: './blog-post.component.html',
  styleUrls: ['./blog-post.component.css'],
  standalone: true,
  imports: [
    MatCardModule, MatButtonModule, CommonModule,
  ],
})
export class BlogPostComponent implements OnInit {
  blogTitle: string | undefined;
  blogBanner: string | undefined;
  mainContent: string | undefined;
  currentPage = 0;
  itemsPerPage = 2;
  totalItems = 0;
  blogData: any;
  categories: any;
  categoryId: string = '';
  activeCategory: string = 'All';
  itemData: any;

 
  constructor(private route: ActivatedRoute, private blogService: BlogService, private router: Router,private dataService: DataService) { }
  ngOnInit(): void {
    this.loadBlogItems(this.currentPage, this.itemsPerPage, this.activeCategory),
  

    this.blogService.getItems()
    .subscribe((data: any) => {
      this.categories = data.data;
      console.log("blog_category",this.categories)
    });
     
   
    this.route.queryParams.subscribe((queryParams) => {
      this.blogBanner = queryParams['banner'];
      this.mainContent = queryParams['main_content'];
      

