import { HttpClient } from '@angular/common/http';
import {
  Component,
  Input,
  OnInit,
  AfterViewInit,
  ViewChild,
  HostListener,
} from '@angular/core';
import { Router, RouterModule} from '@angular/router';
import { environment } from '../../../environments/environment';

@Component({
  selector: 'app-client-logos',
  templateUrl: './client-logos.component.html',
  styleUrls: ['./client-logos.component.css'],
})
export class ClientLogosComponent implements OnInit, AfterViewInit {
  @Input('companyLogos') companyLogos: String[] = [];

  @ViewChild('companyLogosFlex') companyLogosFlexDiv: any = null;
  @Input() data: any; // define the data type based on your API response
  baseUrl = 'http://54.91.234.70:8056';

  displayRightArrow: boolean = false;
  displayLeftArrow: boolean = false;

  isMouseActive: boolean = false;
  mouseXStart = 0;
  scrollLeftStart = 0;
  ASSETS_URL = environment.ASSETS_URL;

  @Input() fetchDataFromAPI = true;


  @HostListener('mousedown', ['$event']) mouseDownEvent(e: MouseEvent) {
    this.isMouseActive = true;
    this.mouseXStart = e.pageX;
    this.scrollLeftStart = this.companyLogosFlexDiv.nativeElement.scrollLeft;
  }

  @HostListener('mouseup', ['$event']) mouseUpEvent(e: MouseEvent) {
    this.isMouseActive = false;
  }

  @HostListener('mousemove', ['$event']) mouseDounEvent(e: any) {
    if (!this.isMouseActive) return;
    e.preventDefault();
    const movementDimenstion = e.pageX - this.mouseXStart;
    this.companyLogosFlexDiv.nativeElement.scrollLeft =
      this.scrollLeftStart - movementDimenstion;
  }

  constructor(private http: HttpClient,private router: Router) {}

  ngOnInit(): void {
    if (this.fetchDataFromAPI)
    {
      this.fetchData();
    }
  }
  fetchData() {
    const url = `${this.baseUrl}items/1st_slider_banner_menu?filter[active_from][_lt]=2023-10-27T15:31:00&filter[active_to][_gt]=2023-10-27T15:30:00`;
    this.http.get(url).subscribe((data: any) => {
      this.data = data.data; 
      console.log("========++++++asedetrfrgrgr",this.data)// handle the response data accordingly
    }, (error) => {
      console.error('Error fetching data:', error);
    });
  }

  ngAfterViewInit(): void {
    setTimeout(() => {
      if (this.companyLogosFlexDiv) {
        if (
          this.companyLogosFlexDiv?.nativeElement?.scrollWidth >
          this.companyLogosFlexDiv?.nativeElement?.clientWidth
        ) {
          this.displayRightArrow = true;
        } else {
          this.displayRightArrow = false;
          this.displayLeftArrow = false;
        }
      }
    }, 100);
  }

  imageContainerScrollEvent() {
    if (this.companyLogosFlexDiv.nativeElement.scrollLeft > 0) {
      this.displayLeftArrow = true;
    } else {
      this.displayLeftArrow = false;
    }

    if (
      Math.round(this.companyLogosFlexDiv.nativeElement.scrollLeft) >
      Math.round(
        this.companyLogosFlexDiv?.nativeElement?.scrollWidth -
          this.companyLogosFlexDiv?.nativeElement?.clientWidth -
          2
      )
    ) {
      this.displayRightArrow = false;
    } else {
      this.displayRightArrow = true;
    }
  }

  scrollWithArrows(action: 'left' | 'right') {
    const scrollFactor = 100;
    if (action === 'left') {
      if (this.companyLogosFlexDiv.nativeElement.scrollLeft > scrollFactor) {
        this.companyLogosFlexDiv.nativeElement.scrollLeft -= scrollFactor;
      } else {
        this.companyLogosFlexDiv.nativeElement.scrollLeft = 0;
      }
    }

    if (action === 'right') {
      if (
        this.companyLogosFlexDiv?.nativeElement?.scrollWidth -
          this.companyLogosFlexDiv?.nativeElement?.clientWidth >
        100
      ) {
        this.companyLogosFlexDiv.nativeElement.scrollLeft += scrollFactor;
      } else {
        this.companyLogosFlexDiv.nativeElement.scrollLeft =
          this.companyLogosFlexDiv?.nativeElement?.scrollWidth;
      }
    }
  }
}

