        this.companyLogosFlexDiv?.nativeElement?.scrollWidth -
          this.companyLogosFlexDiv?.nativeElement?.clientWidth -
          2
      )
    ) {
      this.displayRightArrow = false;
    } else {
      this.displayRightArrow = true;
    }
  }

  scrollWithArrows(action: 'left' | 'right') {
    const scrollFactor = 100;
    if (action === 'left') {
      if (this.companyLogosFlexDiv.nativeElement.scrollLeft > scrollFactor) {
        this.companyLogosFlexDiv.nativeElement.scrollLeft -= scrollFactor;
      } else {
        this.companyLogosFlexDiv.nativeElement.scrollLeft = 0;
      }
    }

    if (action === 'right') {
      if (
        this.companyLogosFlexDiv?.nativeElement?.scrollWidth -
          this.companyLogosFlexDiv?.nativeElement?.clientWidth >
        100
      ) {
        this.companyLogosFlexDiv.nativeElement.scrollLeft += scrollFactor;
      } else {
        this.companyLogosFlexDiv.nativeElement.scrollLeft =
          this.companyLogosFlexDiv?.nativeElement?.scrollWidth;
      }
    }
  }
}

