  gap: 10px;
  line-height: normal;
}
.back-arrow {
  width: 24px;
  height: 24px;
  min-width: 24px;
  border-radius: 50%;
  background: #2c3538;
  display: flex;
  align-items: center;
  justify-content: center;
}
/* For desktop view */
.desktop-view {
  display: flex;
}

/* For mobile view */
.mobile-view {
  display: none;
}

ul {
  list-style-type: none;
  margin: 0;
  padding: 0;
}

li {
  display: inline;
}

.left-arrow-btn,
.right-arrow-btn {
  display: none;
}

@media screen and (max-width: 1024px) {
  .deposit-main-wrapper {
    padding: 24px 50px;
  }
  /* For mobile view */
  .desktop-view {
    display: none;
  }

  /* For desktop view */
  .mobile-view {
    display: flex;
  }
  .mobile-view ul {
    display: flex;
    border-bottom: 1px solid #4d585d;
    margin-bottom: 20px;
  }
  .deposit-main-wrapper.mobile-view .btn-deposit {
    margin-bottom: 0 !important;
  }
  .deposit-main-wrapper {
    min-height: calc(100vh - 32px);
  }
  .payment-wrapper {
    display: flex;
    flex-direction: column;
    flex: 1;
    height: calc(100vh - 178px);
  }
  .payment-account-mobile {
    flex: 1;
    overflow: auto;
  }
}

@media screen and (max-width: 767px) {
  .deposit-main-wrapper {
    padding: 16px;
  }
  .payment-method-wrap .pay-box-wrapper {
    display: flex;
    gap: 25px;
    position: relative;
  }
  .tag-number-wrap {
    margin: 21px 0 16px;
    gap: 20px;
    overflow: auto;
    padding-bottom: 10px;
  }

  .tag-number-wrap::-webkit-scrollbar-track,
  .pay-box-wrapper::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    border-radius: 15px;
    background-color: #18242a;
  }

  .tag-number-wrap::-webkit-scrollbar,
  .pay-box-wrapper::-webkit-scrollbar {
    width: 6px;
    height: 6px;
    background-color: #18242a;
  }

  .tag-number-wrap::-webkit-scrollbar-thumb,
  .pay-box-wrapper::-webkit-scrollbar-thumb {
    border-radius: 15px;
    -webkit-box-shadow: inset 0 0 6px #4d585d;
    background-color: #18242a;
  }

  .tag-number-wrap button {
    border-radius: 10px;
    padding: 0 10px;
    line-height: 35px;
  }
  .tag-number-wrap button.active,
  .tag-number-wrap button:hover {
    padding: 0 10px;
    line-height: 35px;
  }
  .more {
    background-color: #2c3538;
  }
  .left-arrow-btn {
    left: -7px;
  }
  .right-arrow-btn {
    right: -7px;
  }
  .left-arrow-btn,
  .right-arrow-btn {
    display: flex;
    position: absolute;
    top: 50%;
    transform: translateY(-150%);
  }
  .pay-box-wrapper .item-list.active {
    opacity: 1;
  }
  .pay-box-wrapper .item-list {
    opacity: 0.5;
  }
}
@media screen and (max-width: 375px) {
  .tag-number-wrap {
    gap: 8px;
  }
  .tag-number-wrap button {
    padding: 0 5px;
    font-size: 10px;
    line-height: 28px;
  }
  .tag-number-wrap button.active,
  .tag-number-wrap button:hover {
    padding: 0 5px;
    font-size: 10px;
    line-height: 28px;
  }
  .payment-method-wrap .pay-box-wrapper {
    gap: 16px;
  }
}

