import {
  Component,
  ViewChildren,
  QueryList,
  OnInit,
  Input,
  EventEmitter,
  Output
} from '@angular/core';
import { FormControl } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { combineLatest, of, Subject, Subscription } from 'rxjs';
import { map, takeUntil, filter, catchError } from 'rxjs/operators';
import {
  StompHeaders,
  LiveDocService,
  LiveDocConfigLoaderService,
  LiveDocConfig,
} from '@livedoc';
import { APP_LIVEDOC_SETTINGS } from '../../app.settings';
import { AuthMockService } from '../../auth-mock.service';
import { BeeperService } from '../../beeper.service';
import {
  EventData,
  MainEventFeed,
  Participant,
} from '../../dto/event-data.dto';
import { FinHomeCoreData } from '../../dto/home-data.dto';
import { liveDocConfigValidator } from '../../helpers/livedoc-config-validator';
import { EVENT_TYPE, MARKET_TYPE } from '../../util/enum';
import { OddEventData, Selection } from '../../dto/odd-data.dto';
import { BettingService } from '../../services/betting-service';
import { DemoBettingService } from '../../services/demo-betting-service';
import { environment } from 'projects/sportsbook/src/environments/environment.prod';
import { MarketService } from '../../services/market.service';

interface DeltaMergeStrategy {
  label: string;
  function: (a: any, b: any) => any;
}

interface LiveDocCard {
  destination: string;
  data: any;
  headers: StompHeaders;
  // url: string;
  strategy: string;
  updates: number;
  lastData: Date;
  created: Date;
  terminated: Date;
  subscription: Subscription;
  state: 'Connecting' | 'Connected' | 'Unsubscribed' | 'Error';
}

@Component({
  selector: 'app-event-item',
  templateUrl: './event-item.component.html',
  styleUrls: ['./event-item.component.css']
})
export class EventItemComponent {
  @Output() addClassEvent = new EventEmitter<void>();


  @Input() eventId: string;
  @Input() eventData: EventData;
  isSelected: boolean = false;
  singleMarket: boolean = false;
  secondTab: string = 'Slider';
  displayMarket: boolean = false;
  thirdTab: string = 'All';
  isOtherMarketsOpen: boolean;
  betslipData: any = []
  selectedMarket: boolean = false;

  constructor(
    public liveDocService: LiveDocService,
    public liveDockConfigMockLoader: LiveDocConfigLoaderService,
    private httpClient: HttpClient,
    private beeper: BeeperService,
    private auth: AuthMockService,
    private bettingService: BettingService,
    private demoBettingService: DemoBettingService,
    private marketService: MarketService
  ) {
    // console.log('Generating first authentication token automatically for secure sockets');
    // auth.newToken(
    //   'DEFAULT TOKEN: This tester application generates first token automatically, ' +
    //   'otherwise subscriptions to secure sockets would wait for the token'
    // );
    this.marketService.isOtherMarketsOpen$.subscribe((value) => {
      this.isOtherMarketsOpen = value;
    });
  }

  changeCondition() {
    this.marketService.setIsOtherMarketsOpen(false);
    console.log('market Status', this.isOtherMarketsOpen)
  }

  ngOnInit() {
    if (environment.isLiveFeedConnected) {
      this.loadEvent(this.eventData);
    } else {
      this.loadEventDemo(this.eventData);
    }
    this.performOddOperation;
  }

  toggleOtherMarketsClass() {
    this.isOtherMarketsOpen = !this.isOtherMarketsOpen;
    this.singleMarket = true;
    localStorage.setItem('singleMarket', JSON.stringify(this.singleMarket));
    this.addClassEvent.emit();
  }


  marketId: string;

  private unsubscribeAll = new Subject<void>();

  destinations = [
    'echo/echo',
    'notifications/alerts',
    'events/E-1~20',
    'sports/',
    'competitions/',
    'markets/',
    'eventmarkets/',
    'fieldbook/',
    'rma/bettickers',
  ];

  private readonly unsubscribe$: Subject<void> = new Subject();

  headers: StompHeaders;

  homeFeedLink: string = 'eventmap/upcomingFBL';
  eventsFeedLink: string = 'events/';
  marketsFeedLink: string = 'markets/';

  homeData: FinHomeCoreData;
  mainEventFeedData: MainEventFeed[];
  oddsEventData: OddEventData;
  mainEventData: EventData;
  eventType: EVENT_TYPE = EVENT_TYPE.EVENT_FEED;
  participants: Participant[] = [];
  selections: Selection[] = [];
  preSelections: Selection[] = [];
  oddsSlider1: string = '1';
  oddsSlider1Selected: number | null = 1;
  oddsSlider2: string = '4';
  oddsSlider2Selected: number | null = 4;

  homeWinOdd: string = '0.00';
  awayWinOdd: string = '0.00';
  teamDrawOdd: string = '0.00';

  homeTeam: string = 'NGN';
  awayTeam: string = 'CMR';

  gameDate: string;
  gameTime: string;

  liveDocCards: LiveDocCard[] = [];
  staleCardCount = 0;

  marketType: MARKET_TYPE = MARKET_TYPE.MAIN;

  liveDocConfigUser = new FormControl('', liveDocConfigValidator());


  onThirdTabClick(tab: string) {
    this.thirdTab = tab;
  }

  updateValue(value: number, index: number) {
    this.oddsSlider1 = value.toString();
    this.oddsSlider1Selected = index;
  }

  updateValue2(value: number, index: number) {
    this.oddsSlider2 = value.toString();
    this.oddsSlider2Selected = index;
  }

  onSecondTabClick(tab: string) {
    this.secondTab = tab;
  }




  // Put LiveDoc config into the input for the user to modify
  liveDocConfigFile: string;
  liveDocConfigSubscription = this.httpClient
    .get<LiveDocConfig>(APP_LIVEDOC_SETTINGS.configUrl)
    .pipe(
      map((config) => JSON.stringify(config, null, 4)),
      catchError((e) => of('Error in the config file')),
      takeUntil(this.liveDocConfigUser.valueChanges)
    )
    .subscribe((config) => {
      console.log('COnfig', config);
      this.liveDocConfigUser.setValue((this.liveDocConfigFile = config));
    });

  destinationControl = new FormControl(this.destinations[0]);

  destinationSuggestions = combineLatest([
    of(this.destinations),
    this.destinationControl.valueChanges,
  ]).pipe(
    map(([destinations, prefix]) =>
      destinations.filter((destination) => destination.includes(prefix))
    )
  );

  deltaMergeStrategies: DeltaMergeStrategy[] = [
    {
      label: 'Show Diff',
      function: (last: any, next: any) => ('Diff' ? next : last),
    },
    {
      label: 'Show Grow',
      function: (last: any, next: any) => ('Grow' ? next : last),
    },
    {
      label: 'Show Change',
      function: (last: any, next: any) => ('Change' ? next : last),
    },
    {
      label: 'Show Average',
      function: (last: any, next: any) => ('Average' ? next : 0),
    },
  ];

  chosenStrategy = new FormControl();

  unsubscribeOthersControl = new FormControl();
  beepControl = new FormControl();

  updateLiveDocConfig(configText: string) {
    const userConfig = JSON.parse(configText);

    // The really awkward line below is to make testing easier
    (this.liveDocService as any).livedocConfigLoaderService.newConfig.next(
      userConfig
    );
  }

  // rippleCard(card: LiveDocCard, color?: string) {
  //   const cardIndex = this.liveDocCards.findIndex(c => c === card);
  //   if ( cardIndex > -1 ) {
  //     const ripple = this.ripples.find((r, i) => i === cardIndex);
  //     if ( ripple ) {
  //       ripple.launch({
  //         centered: Math.random() < .5,
  //         color
  //       });
  //     }
  //   }
  // }

  performOddOperation(selection: Selection) {
    console.log('selection', selection);
    this.isSelected = !this.isSelected;
    this.selections.find((item) => item.id == selection.id).isSelected = !this.selections.find((item) => item.id == selection.id).isSelected
    // console.log('this.selections', this.selections)

    this.selections.find((item) => item.id == selection.id).isSelected
      ? this.bettingService.addToBetSlip(selection)
      : this.bettingService.deleteToBetSlip(selection);
  }

  betsData() {
    const betslipData = localStorage.getItem('zebet_betslip_key_dashboard');
    const convertedBetslipData = JSON.parse(betslipData).data;

    console.log('convertedBetslipData', convertedBetslipData);
    console.log('this.selections', this.selections);

    for (const item of this.selections) {
      var newItem = {
        ...item,
        isSelected: true
      }
      this.preSelections.push(newItem);
    }

    this.selections = this.preSelections;
    console.log('this.preSelections', this.preSelections)
  }

  subscribeAllDestinations(
    destinationWithDelta: string,
    deltaHandlingStrategy?: DeltaMergeStrategy,
    headers?: StompHeaders,
    unsubscribeOthers?: boolean
  ) {
    console.log('Headers', headers);
    console.log('deltaHandlingStrategy', deltaHandlingStrategy);
    if (unsubscribeOthers) {
      this.unsubscribeAll.next();
    }

    const range = destinationWithDelta.match(/^(.*?)(\d+)~(\d+)(.*)$/);
    if (range) {
      const prefix = range[1];
      const start = +range[2];
      const end = +range[3];
      const suffix = range[4];
      for (let i = start; i <= end; ++i) {
        const destination = prefix + i + suffix;
        this.newSubscription(destination, deltaHandlingStrategy, headers);
      }
    } else {
      this.newSubscription(
        destinationWithDelta,
        deltaHandlingStrategy,
        headers
      );
    }
  }

  subscribeOddDestinations(
    destinationWithDelta: string,
    deltaHandlingStrategy?: DeltaMergeStrategy,
    headers?: StompHeaders,
    unsubscribeOthers?: boolean
  ) {
    // console.log("Headers", headers);
    // console.log("deltaHandlingStrategy", deltaHandlingStrategy);
    // if ( unsubscribeOthers ) { this.unsubscribeAll.next(); }

    // const range = destinationWithDelta.match(/^(.*?)(\d+)~(\d+)(.*)$/);
    // if ( range ) {
    //   const prefix = range[1];
    //   const start = +range[2];
    //   const end = +range[3];
    //   const suffix = range[4];
    //   for (let i = start; i <= end; ++i) {
    //     const destination = prefix + i + suffix;
    //     this.newOddSubscription(destination, deltaHandlingStrategy, headers);
    //   }
    // } else {
    this.newOddSubscription(
      destinationWithDelta,
      deltaHandlingStrategy,
      headers
    );
    // }
  }

  newSubscription(
    destination: string,
    deltaHandlingStrategy?: DeltaMergeStrategy,
    headers?: StompHeaders
  ) {
    console.group('New Subscription Parameters:');
    console.table({
      destination,
      headers:
        Object.entries(headers || {})
          .map(([key, value]) => key + ' : ' + value)
          .join(';   ') || null,
      deltaMerge: deltaHandlingStrategy && deltaHandlingStrategy.label,
    });
    console.groupEnd();

    const card: LiveDocCard = {
      destination,
      data: null,
      headers,
      // url: ;
      strategy: (deltaHandlingStrategy && deltaHandlingStrategy.label) || '',
      updates: 0,
      lastData: null,
      created: new Date(),
      terminated: null,
      subscription: null,
      state: 'Connecting',
    };

    const stream = this.liveDocService.getStream({
      document: destination,
      headers,
      deltaHandlingStrategy:
        deltaHandlingStrategy && deltaHandlingStrategy.function,
    });

    const subscription = stream.pipe(takeUntil(this.unsubscribe$)).subscribe({
      next: (data) => {
        this.unsubscribe$.next();
        this.unsubscribe$.complete();

        //console.log("Data Received",JSON.stringify(data, null, 2));

        switch (this.eventType) {
          case EVENT_TYPE.HOME_FEED:
            break;

          case EVENT_TYPE.EVENT_FEED:
            //console.log("Data Count")
            this.mainEventData = JSON.parse(JSON.stringify(data, null, 2));
            //console.log("Live Data", this.mainEventData);
            const raw = this.mainEventData;

            console.log('testsets >>...', raw?.participants[0][0]);
            this.homeTeam = this.mainEventData.participants[0][0]?.name;
            this.awayTeam = this.mainEventData.participants[0][1]?.name;
            this.gameTime = this.mainEventData.anticipated.startTime
              .split('T')[1]
              .substring(0, 5);
            this.marketId = this.mainEventData.miniCoupons['TOT1545'][0];
            this.gameDate =
              (new Date(this.mainEventData.anticipated.startTime)).toLocaleDateString(
                'en-GB',
                {
                  day: '2-digit',
                  month: '2-digit',
                }
              );

            console.log('game date >>', this.gameDate);

            this.subscribeOddDestinations(
              `${this.marketsFeedLink}${this.marketId}`,
              null,
              this.headers,
              false
            );

            break;
        }
      },
      error: (e) => {
        card.terminated = new Date();
        card.state = 'Error';
        //this.rippleCard(card, 'rgba(255, 0, 0, 0.2)');
        ++this.staleCardCount;
      },
      complete: () => {
        card.terminated = new Date();
        card.state = 'Unsubscribed';
        ++this.staleCardCount;
      },
    });

    card.subscription = subscription;

    this.liveDocCards.unshift(card);
  }

  loadEvent(eventData: EventData) {
    this.homeTeam = eventData.participants[0][0]?.name;
    this.awayTeam = eventData.participants[0][1]?.name;
    this.gameTime = eventData.anticipated.startTime
      .split('T')[1]
      .substring(0, 5);
    this.gameDate = (new Date(eventData.anticipated.startTime)).toLocaleDateString(
      'en-GB',
      {
        day: '2-digit',
        month: '2-digit',
      }
    );
    this.marketId = eventData.miniCoupons['MAIN'][0];

    console.log('market id', this.marketId);
    this.bettingService
      .getMarkets(`${this.marketsFeedLink}${this.marketId}`)
      .pipe(takeUntil(this.unsubscribeAll))
      .subscribe({
        next: (data) => {
          this.oddsEventData = JSON.parse(JSON.stringify(data, null, 2));
          this.homeWinOdd = this.oddsEventData.selections[0][0].price.dec;
          this.teamDrawOdd = this.oddsEventData.selections[0][1].price.dec;
          this.awayWinOdd = this.oddsEventData.selections[0][2].price.dec;

          const betslipData = localStorage.getItem('zebet_betslip_key_dashboard');
          const convertedBetslipData = JSON.parse(betslipData).data;

          this.oddsEventData.selections[0].forEach((data) => {
            switch (this.marketType) {
              case MARKET_TYPE.MAIN:
                data.marketType = '1X2';
                break;

              case MARKET_TYPE.OVER25:
                data.marketType = 'ov2.5';
                break;
            }

            data.eventId = eventData.id;
            data.eventName = this.homeTeam + ' vs ' + this.awayTeam;

            for (const item of convertedBetslipData) {
              if (item.eventId == eventData.id) {
                if (item.side == data.side) {
                  data.isSelected = true;
                }
              }
            }

            this.selections.push(data);
          });
        },
        error: (e) => { },
        complete: () => { },
      });

    //this.subscribeOddDestinations(`${this.marketsFeedLink}${this.marketId}`, null, this.headers, false);
  }

  loadEventDemo(eventData: EventData) {
    this.homeTeam = eventData.participants[0][0]?.name;
    this.awayTeam = eventData.participants[0][1]?.name;
    this.gameTime = eventData.anticipated.startTime
      .split('T')[1]
      .substring(0, 5);
    this.gameDate = (new Date(eventData.anticipated.startTime)).toLocaleDateString(
      'en-GB',
      {
        day: '2-digit',
        month: '2-digit',
      }
    );
    this.marketId = eventData.miniCoupons['MAIN'][0];

    this.demoBettingService
      .getMarkets()
      .pipe()
      .subscribe({
        next: (data) => {
          console.log('Odd Data Received 2', JSON.stringify(data, null, 2));

          this.oddsEventData = JSON.parse(JSON.stringify(data, null, 2));

          console.log('debug Data', this.oddsEventData.selections[0][0]);

          this.homeWinOdd = this.oddsEventData.selections[0][0].price.dec;
          this.teamDrawOdd = this.oddsEventData.selections[0][1].price.dec;
          this.awayWinOdd = this.oddsEventData.selections[0][2].price.dec;
          this.oddsEventData.selections[0].forEach((data) => {
            switch (this.marketType) {
              case MARKET_TYPE.MAIN:
                data.marketType = '1X2';
                break;

              case MARKET_TYPE.OVER25:
                data.marketType = 'ov2.5';
                break;
            }

            data.eventId = eventData.id;
            data.eventName = this.homeTeam + ' vs ' + this.awayTeam;
            this.selections.push(data);
          });
          //this.selections.push(...this.oddsEventData.selections[0]);
        },
        error: (e) => { },
        complete: () => { },
      });

    //this.subscribeOddDestinations(`${this.marketsFeedLink}${this.marketId}`, null, this.headers, false);
  }

  newOddSubscription(
    destination: string,
    deltaHandlingStrategy?: DeltaMergeStrategy,
    headers?: StompHeaders
  ) {
    console.group('New Subscription Parameters:');
    console.table({
      destination,
      headers:
        Object.entries(headers || {})
          .map(([key, value]) => key + ' : ' + value)
          .join(';   ') || null,
      deltaMerge: deltaHandlingStrategy && deltaHandlingStrategy.label,
    });
    console.groupEnd();

    const card: LiveDocCard = {
      destination,
      data: null,
      headers,
      // url: ;
      strategy: (deltaHandlingStrategy && deltaHandlingStrategy.label) || '',
      updates: 0,
      lastData: null,
      created: new Date(),
      terminated: null,
      subscription: null,
      state: 'Connecting',
    };

    const stream = this.liveDocService.getStream({
      document: destination,
      headers,
      deltaHandlingStrategy:
        deltaHandlingStrategy && deltaHandlingStrategy.function,
    });

    const subscription = stream.pipe(takeUntil(this.unsubscribeAll)).subscribe({
      next: (data) => {
        console.log('Odd Data Received 3', JSON.stringify(data, null, 2));

        this.oddsEventData = JSON.parse(JSON.stringify(data, null, 2));

        console.log('debug Data', this.oddsEventData.selections[0][0]);

        this.homeWinOdd = this.oddsEventData.selections[0][0].price.dec;
        this.teamDrawOdd = this.oddsEventData.selections[0][1].price.dec;
        this.awayWinOdd = this.oddsEventData.selections[0][2].price.dec;
      },
      error: (e) => {
        card.terminated = new Date();
        card.state = 'Error';
        //this.rippleCard(card, 'rgba(255, 0, 0, 0.2)');
        ++this.staleCardCount;
      },
      complete: () => {
        card.terminated = new Date();
        card.state = 'Unsubscribed';
        ++this.staleCardCount;
      },
    });

    card.subscription = subscription;

    this.liveDocCards.unshift(card);
  }

  loadHomeEvents(data: EventData) {
    this.eventType = EVENT_TYPE.EVENT_FEED;
    this.subscribeAllDestinations(
      `${this.eventsFeedLink}${data.id}`,
      null,
      this.headers,
      false
    );
  }

  unsubscribeCard(card: LiveDocCard) {
    card.subscription.unsubscribe();
    this.staleCardCount++;
    card.state = 'Unsubscribed';
  }

  removeCard(card: LiveDocCard) {
    // if ( card.state === 'Error' || card.state === 'Unsubscribed' ) {
    --this.staleCardCount; // Only stale can be removed
    this.liveDocCards = this.liveDocCards.filter(
      (candidate) => candidate !== card
    );
  }

  removeAll() {
    this.unsubscribeAll.next();
    this.liveDocCards = [];
    this.staleCardCount = 0;
  }

  removeStale() {
    this.liveDocCards = this.liveDocCards.filter(
      (card) => card.state === 'Connecting' || card.state === 'Connected'
    );
    this.staleCardCount = 0;
  }

  // send() {
  //   this.dialog.open<StompSenderComponent, StompSenderIO, StompSenderIO>(StompSenderComponent, {
  //     data: {
  //       destination: this.destinationControl.value,
  //       headers: this.headers,
  //       body: ''
  //     },
  //     disableClose: true
  //   }).afterClosed().pipe(
  //     filter(reply => !!reply)
  //   ).subscribe(reply => this.liveDocService.send(reply));
  // }

  loadNewConfig(newConfig: string) {
    this.liveDockConfigMockLoader.newConfig.next(JSON.parse(newConfig));
  }

  // newToken() {
  //   this.dialog.open<StompTokensDialogComponent, any, string>(StompTokensDialogComponent).afterClosed().pipe(
  //     filter( reply => !!reply)
  //   ).subscribe(token => this.auth.newToken(token));
  // }

  showMarket() {
    this.displayMarket = !this.displayMarket
  }

