	constructor(private http: HttpClient, private dialog: MatDialog, private countryService: CountryService, private cdr: ChangeDetectorRef, private router: Router) {
		this.SendOtp = environment.Send_OTP;
		this.VerifyOtp = environment.Verify_OTP
	}
	/// Send Verification OTP
	async otpSend() {
		let otpBody = {};
		let sendOtp = {};
		console.log('============ send code');
		if (this.actType == "2") {
			otpBody = {
				phone_number: this.phone_number,
				country_code: this.selectedCountryCode,
				is_voice_call: 0,
				is_forgot_password: 1,
			};
			sendOtp = {
				sms_otp: this.sms_otp
			};

		}
		else {
			otpBody = {
				email: this.user_email,
				is_forgot_password: 1,
			};
			sendOtp = {
				voice_otp: this.voice_otp
			};
		}

		const url = this.SendOtp;
		const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
		try {
			const response = await this.http.post(url, otpBody, { headers }).toPromise();
			if (!this.selectedOption) {
				this.showValidationError = true;
			} else {

				this.showValidationError = false;

			}


			if (response) {
				this.responseData = response;
				console.log(this.responseData);
				console.log('Response from OTP API:', response);
				if (this.responseData.message_key == 'OTP Sent Successfully') {
					this.toggleSubmitVerifyOtpButton('1')
					alert(this.responseData.message_key);
				}
				else {
					this.otpCode = this.responseData.data.otpCode;
					this.toggleSubmitVerifyOtpButton("1");
				}

			} else {
				this.errorMessage = 'Failed to make the request';
				alert(this.responseData.message_key);

			}
		} catch (error) {
			this.errorMessage = 'An error occurred: ' + error;

		}
	}
	//// Otp Verification
	async verifyOTP() {
		this.verifyOtpBorder = "";
		this.verifyOtpMessage = "";
		let otpData = {};
		console.log('============ verify opt');
		if (this.actType == "2") {
			otpData = {
				phone_number: this.phone_number,
				country_code: this.selectedCountryCode,
				otp_code: this.verifyOtp
			};
		} else {
			otpData = {
				email: this.user_email,
				otp_code: this.verifyOtp
			};
		}
		this.http.post(this.VerifyOtp, otpData).subscribe(
			(response: any) => {
				if (response.status == 'SUCCESS' && response.message_key == 'OTP Verified Successfully') {
					console.log('OTP verified successfully');
					this.otpVerified = true;
					this.togglePasswordVisibility();
				} else {
					this.verifyOtpMessage = 'An error occurred during OTP verification';
					this.verifyOtpBorder = "border-red";
				}
			}, (error) => {
				this.verifyOtpMessage = "Please enter valid number";
				this.verifyOtpBorder = "border-red";
			});
	}
	//// Sign Up process
	async foreget() {
		const url = environment.Forget_Password;
		const headers = new HttpHeaders({
			'Content-Type': 'application/json'
		});
		let body = {};
		console.log(this.actType);
