    let sizes: SCREEN_SIZE = SCREEN_SIZE.LG;
    if (width >= 0 && width <= 640) sizes = SCREEN_SIZE.XS;
    else if (width >= 641 && width <= 1007) sizes = SCREEN_SIZE.MD;
    else if (width >= 1008) sizes = SCREEN_SIZE.LG;

    return sizes;
  }
}

