  // }
}

import { Component, OnInit, ElementRef, HostListener } from '@angular/core'; 
import { HttpHeaders, HttpClient } from '@angular/common/http';
import { Router,NavigationEnd, ActivatedRoute } from '@angular/router';
import { ApiService } from './api.service';
import { AuthService } from '../../auth/auth.service';
import { environment } from 'projects/sportsbook/src/environments/environment';


interface ApiResponse {
  status: string;
  message_key?: string;
}
@Component({
  selector: 'app-header', 
  templateUrl: './header.component.html', 
  styleUrls: ['./header.component.css'], 
  
})
export class HeaderComponent implements OnInit {
  storedData :any;
  actType: string = '1'; 
  phoneNumber: string = '';
  selectedCountryCode: string = '';
  user_email: string = '';
  responseData: any; 
  errorMessage: string = '';
  password: string = ''; 
  topNavItems: any;
  topNavlogo:any;
  error: any;
  isLoggedIn: boolean = true;
  isEmailSelected : boolean = true;
  isPhoneNumberSelected: boolean = true;
  loginMethod: string = 'email';
  selectedOption: string = '1';
  countyCode: string = "+234";
  loginCode: string = "loginCode";
  isMenuVisible: boolean = false;
  isMediumScreen: boolean = false;
  isSplashPage: boolean = false;
  isRegisterAccountRoute: boolean = false;
  isBlogAvtive: string = "";
  ASSETS_URL = environment.ASSETS_URL;
   
  toggleInputType() {
    console.log('Selected option:', this.selectedOption);
    if(this.selectedOption=="2") {
      this.isEmailSelected = false;
    }  
    else {
      this.isEmailSelected = true;
    }
  }
  isBlogActive: boolean = false;
  constructor(private http: HttpClient, private router: Router, private apiService:ApiService, private el: ElementRef,private authService: AuthService, private route: ActivatedRoute) { 
    this.storedData = localStorage.getItem('accessToken');
    this.checkScreenWidth();
    this.isLoggedIn = this.authService.isLoggedIn(); 
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        const url = this.router.url;

        const currentUrl = this.router.url;
        if (currentUrl.includes('')) {
          this.isBlogAvtive = '';
            console.log('String found in URL');
          } 
        const searchString = 'blog';    
        if (currentUrl.includes(searchString)) {
          this.isBlogAvtive = 'Blog';
          console.log('String found in URL');
        }
        if (currentUrl.includes('promotion')) {
        this.isBlogAvtive = 'Promotion';
          console.log('String found in URL');
        } 
        
      }
    });
  }

    
  @HostListener('window:resize', ['$event'])
  onResize(event: any): void {
    this.checkScreenWidth();
  }

  checkScreenWidth(): void {
    // Determine the screen width and set isMediumScreen accordingly
    this.isMediumScreen = window.innerWidth >=768 && window.innerWidth <= 1265;

    // Hide the menu when screen size changes to a size other than medium
    if (!this.isMediumScreen) {
      this.isMenuVisible = false;
    }
  }

  toggleMenu() {
    this.isMenuVisible = !this.isMenuVisible;
  }

  

  
  // login with header//
  async login() {
    const url = environment.Log_In_API;
    const accessToken = this.authService.getAccessToken();
    const headers = new HttpHeaders({ 'Content-Type': 'application/json', 'Authorization': `Bearer ${accessToken}`, });


    let body = {};
    let error = {};
    console.log(this.selectedOption);
    if (this.selectedOption === "2") {

      const phone_number = document.getElementById('phoneNumber') as HTMLInputElement; 
      const loginType = document.getElementById('loginCode') as HTMLInputElement;    
      body = {
        email: "",
        phone_number: phone_number.value,
        country_code: loginType.value,
        password: this.password,   
      };
    } else {  
      body = {
        email: this.user_email,
        phone_number: "",
        password: this.password,    
      };  
    } 

    this.http.post<any>(url, body, { headers })
    .subscribe(
      (response) => {
        console.log('Response:', response);
  
        if (response.status === 'SUCCESS'  ) {
       
          localStorage.setItem('accessToken', response.data.access_token);
          // Redirect or perform any necessary actions upon successful login
          this.router.navigate(['/sports-book']);
          this.isLoggedIn = true;
          setTimeout(() => {
            location.reload();
          }, 0);
        }
      },
      (error) => {
        if (error.status === 401) {
          this.errorMessage=error.error.message_key;
        } else {
          this.router.navigate(['/login-account']);
        }
      }
    );
    
  }
 
  // Top navBar//
  ngOnInit(): void {
    
    this.storedData = localStorage.getItem('accessToken');
     
    this.apiService.getTopNavItems().subscribe(
      (data) => {
        this.topNavItems = data;
        this.topNavlogo = data.data;
        console.log("00000",this.topNavlogo)
      },
      (error) => {
        this.error = error; // Store the error for debugging
        console.error('An error occurred:', error);
      }
    );

    // Use NavigationEnd and cast event to NavigationEnd type
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        
        this.isSplashPage = event.url === '/';
      }

    });
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.isRegisterAccountRoute = event.url.includes('/register-account');
      }
    });

    // this.authService.isLoggedIn$.subscribe((isLoggedIn) => {
    //   this.isLoggedIn = isLoggedIn;
    // });
 
  }



  logout() {
    const signOutUrl =
      environment.Sign_Out_API;

    const access_token = localStorage.getItem('accessToken');

    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      Authorization: `Bearer ${access_token}`
    });

    this.http.post<ApiResponse>(signOutUrl, {}, { headers }).subscribe(
      (response) => {
        console.log('Sign-Out Response:', response);

        if (response.status === 'SUCCESS') {
          localStorage.removeItem('accessToken');
          this.isLoggedIn = false;
          this.router.navigate(['/login-account']);
          setTimeout(() => {
            location.reload();
          }, 0);
        } else {
          console.error('Sign-Out failed:', response);
          alert('Sign-Out Failed. Please try again.'); // You can customize this message or handle the error differently
        }
      },
      (error) => {
        console.error('Sign-Out Error:', error);
        alert('Sign-Out Failed. Please try again.'); // You can customize this message or handle the error differently
      }
    );
  }

}

