import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-jackpot-games-bar',
  templateUrl: './jackpot-games-bar.component.html',
  styleUrls: ['./jackpot-games-bar.component.css']
})
export class JackpotGamesBarComponent implements OnInit {
  betslip = false;
  events = false;

  pastWinners = false;
  topWinners = false;

  activeTab:string = "";
  activeWinnersTab:string = "";
  tabs  : string[] = ["Football Jackpots", "Betslip"];
  winnersTab  : string[] = ["Top Winners", "Last Winners"];
  isUpper  : boolean = false;
  isJustify  : boolean = false;
  isDarkBg  : boolean = false;
  isFromDate: boolean = false;
  isToDate: boolean = false;
  isRules: boolean = false;
  isPreviousResults: boolean = false;
  isWinners: boolean = false;

  calendarDates: number[] = [];
  selectedDate: Date;
  formattedDate: string;
  showFilterModal = true;
  constructor() {
    for (let i = 1; i <= 30; i++) {
      this.calendarDates.push(i);
    }
    this.selectDate(25);
  }

  selectDate(date) {
    this.selectedDate = new Date(2023, 8, date);
    const day = this.selectedDate.getDate();
    const month = this.selectedDate.toLocaleString('default', {
      month: 'short',
    });
    const year = this.selectedDate.getFullYear();

    this.formattedDate = `${day}th ${month} ${year}`;
  }

  ngOnInit() {
    this.events = true;

    if (!this.activeTab && this.tabs != null && this.tabs.length > 0)
    {
        this.activeTab = this.tabs[0];
    }

    this.toggleTab(0, "Football Jackpots")
    this.toggleWinnersTab(0, "Top Winners")
    console.log('tabs', this.tabs)
  }

  eventClick() {
    this.events = true;
    this.betslip = false;
  }
  betslipClick() {
    this.events = false;
    this.betslip = true;
  }

  toggleTab(index: number, tab:string){
    if (this.activeTab != tab)
    {
      this.activeTab = tab;
    }

    if(index == 0)
    {
      this.events = true;
      this.betslip = false;
    }
    else
    {
      this.events = false;
      this.betslip= true;
    }
      

  }

  toggleWinnersTab(index: number, tab:string){
    if (this.activeWinnersTab != tab)
    {
      this.activeWinnersTab = tab;
    }

    if(index == 0)
    {
      this.topWinners = true;
      this.pastWinners = false;
    }
    else
    {
      this.topWinners = false;
      this.pastWinners = true;
    }
      

  }

  isDateActive(date: number) {
    if (this.selectedDate) {
      const selected = this.selectedDate.getDate();
      return date === selected;
    }
    return false;
  }

  showDate(dateType: string){
    switch(dateType)
    {
      case "From":
        this.isFromDate = !this.isFromDate;
        break;

        case "To":
          this.isToDate = !this.isToDate;
          break;
    }
    
  }

  swapRules(){
    this.isRules = !this.isRules;
  }

  swapPreviousResults(){
    this.isPreviousResults = !this.isPreviousResults;
  }

  swapWinners(){
    this.isWinners = !this.isWinners;
  }
}

