.title-tab-main .menu-wrapper span {
  display: none;
}
.topnav .nxt-arrow {
  width: 24px;
  height: 24px;
  min-width: 24px;
  background: #18242a;
  border-radius: 50%;
  display: none;
}
@media screen and (max-width: 1024px) {
  .main-wrapper-account {
    display: none;
  }
  .topnav {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    border: 0;
    gap: 18px;
  }
  .topnav ul {
    flex-direction: column;
    gap: 10px;
  }
  .topnav ul a {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-radius: 10px;
    background: var(--Secondary---Other-Spaces, #2c3538);
    padding: 11px;
    gap: 20px;
    height: 54px;
    border-bottom: 0;
  }
  .title-tab-main .menu-wrapper {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: center;
  }
  .topnav ul a.active {
    border-bottom: 0;
  }
  .title-tab-main {
    display: flex;
    gap: 10px;
  }
  .icon {
    width: 32px;
    height: 32px;
    min-width: 32px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .title-tab-main .menu-wrapper h5 {
    line-height: normal;
    font-size: 16px;
  }
  .title-tab-main .menu-wrapper span {
    color: var(--Primary-Red---Thunderbird, #ffc600);
    font-size: 12px;
    font-style: normal;
    font-weight: 400;
    line-height: 16px;
  }

  .topnav ul .title-tab-main .icon {
    display: flex;
  }
  .title-tab-main .menu-wrapper span {
    display: flex;
  }
  .topnav ul .nxt-arrow {
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .topnav ul .logOut .nxt-arrow {
    background: #be202f;
  }
  .user-account-details .stats {
    flex: 1;
    grid-template-columns: repeat(1, 1fr);
  }
  .stats-desc {
    padding: 7px 0 7px 28px;
  }
  .stats-desc:nth-of-type(2) {
    border-bottom: 1px solid #fff;
    border-top: 1px solid #fff;
  }
}
@media screen and (max-width: 767px) {
  .user-account-details .user-name {
    display: flex;
    flex-direction: column;
  }
  .head-tag {
    margin-bottom: 0;
  }
  .topnav {
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    border: 0;
    gap: 10px;
  }
}

@media screen and (max-width: 374px) {
  .main-wrapper-profile .user-account-details {
    width: 100%;
    display: flex;
    flex-direction: column;
    padding: 16px;
  }
  .user-account-details .user-name {
    width: 100%;
    margin-bottom: 16px;
  }
  .user-account-details .stats {
    width: 100%;
  }
  .title-tab-main .menu-wrapper h5 {
    font-size: 14px;
  }
  .title-tab-main .menu-wrapper span {
    font-size: 10px;
  }
}

