export class RegistrationComponent implements OnInit {
	storedData = localStorage.getItem('accessToken');
	showOtp = false;
	showSubmitOtpButton = true;
	showVerifyOtp = false;
	showSubmitVerifyOtpButton = false;
	isLoading = false;
	showPassword = false;
	hideAfterActivePassword = true;
	hidetext = true;
	otpVerified = false;
	countryCodes: any[] = [];
	isPasswordVisible: boolean = false;
	isconfirmPasswordVisible: boolean = false;
	selectedCountryCode = '+234';
