		console.log(event);
		this.verifyOtp = event;
	}

	// toggleSubmitVerifyOtpButton(step: string): Observable<any> {
	toggleSubmitVerifyOtpButton(step: string) {
		if (step == "1") {
			this.showSubmitVerifyOtpButton = true;
			this.showOtp = false;
			this.showVerifyOtp = true;
			this.isPhoneNumberValid = false;
			this.isEmailValid = false;
			this.showSubmitOtpButton = false;
		}
	}
	togglePasswordVisibility() {
		this.showOtp = false;
		this.showVerifyOtp = false;
		this.hideAfterActivePassword = false;
		this.showPassword = !this.showPassword;
		this.showSubmitVerifyOtpButton = true;
	}
	// --------------dropdown---------------
