.calendar-days {
  display: grid;
  grid-template-columns: repeat(7, 40px);
  text-align: center;
  grid-auto-rows: minmax(40px, 1fr);
  color: var(--White-Primary, #fff);
  font-size: 14px;
  font-weight: 400;
  gap: 10px;
  line-height: 20px;
}

.calendar-day {
  padding: 5px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  box-sizing: border-box;
}
.calendar-day.active {
  color: #18242a;
  cursor: pointer;
  background-color: #ffc600;
  border-radius: 50%;
}

.custom-date-wrap input[type="date"] {
  border-radius: 10px;
  background: var(--Secondary---Other-Spaces, #2c3538);
  border: none;
  outline: none;
  color: #4d585d;
}
.custom-ele-box {
  border-radius: 10px;
  background: #2c3538;
  padding: 12px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
}

.date:focus {
  color: var(--Secondary--2-Trout, #4d585d);
  font-size: 14px;
  font-weight: 500;
  line-height: 20px;
}
.date {
  color: #fff;
  font-size: 14px;
  font-weight: 500;
  line-height: 20px;
}
.date-piker-icon {
  position: absolute;
  width: 22px;
  height: 22px;
  border-radius: 50%;
  background: #18242a;
  display: flex;
  justify-content: center;
  align-items: center;
  top: 50%;
  transform: translateY(-50%);
  left: 9.5px;
  z-index: 0;
}
.date::-webkit-calendar-picker-indicator {
  opacity: 0.01;
  width: 100%;
}

.date::-moz-calendar-picker-indicator {
  opacity: 0.01;
  width: 100%;
}

.date::-ms-clear {
  opacity: 0.01;
  width: 100%;
  height: 100%;
}
.modal {
  display: none;
}
@media screen and (max-width: 767px) {
  .modal {
    position: fixed;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex !important;
    align-items: flex-end;
  }

  .modal-content {
    border-radius: 20px 20px 0px 0px;
    border: 1px solid #4d585d;
    background: #18242a;
    box-shadow: -1px 4px 34px 9px rgba(0, 0, 0, 0.65);
    max-width: 100%;
    padding: 20px;
    position: relative;
    height: calc(100vh - 130px);
    width: 100%;
    margin-top: 0;
  }
  .modal-wrapper {
    display: flex;
    flex-direction: column;
    flex: 1;
    height: 100%;
    max-width: 920px;
    margin: 0 auto;
  }
  .modal-wrapper .modal-body {
    flex: 1;
    overflow: auto;
  }
  .modal-wrapper .footer-action {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 16px;
    margin: 0;
    padding: 0;
  }
  .footer-action .btn-cancel {
    border-radius: 10px;
    background: #2c3538;
    color: #ffc600;
    font-size: 16px;
    font-style: normal;
    font-weight: 600;
    flex: 1;
    line-height: 48px;
    text-transform: uppercase;
  }
  .footer-action .btn-cancel:hover {
    background: #ffc600;
    color: #2c3538;
  }
  .footer-action .btn-save {
    border-radius: 10px;
    background: #ffc600;
    color: #18242a;
    font-size: 16px;
    font-weight: 600;
    line-height: 48px;
    flex: 1;
    text-transform: uppercase;
  }
  .footer-action .btn-save:hover {
    background: #dfaf04;
    color: #18242a;
  }

  .calendar-mobile {
    width: 24px;
    height: 24px;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: #2c3538;
  }
  .mobile-cal-piker {
    border-radius: 10px;
    background: var(--Secondary---Other-Spaces, #2c3538);
    padding: 0 12px;
    display: flex;
    gap: 11px;
    align-items: center;
    border: 1px solid transparent;
  }
  .mobile-cal-piker:hover,
  .mobile-cal-piker.active {
    border: 1px solid #dfaf04;
  }
  .mobile-cal-piker .icon {
    background: #18242a;
    width: 24px;
    height: 24px;
    min-width: 24px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .mobile-cal-piker input:focus,
  .mobile-cal-piker input:focus-within {
    background: transparent;
    color: var(--Secondary--2-Trout, #4d585d);
    font-size: 14px;
    font-style: normal;
    font-weight: 500;
    line-height: 45px;
  }
  .mobile-cal-piker input {
    background: transparent;
    color: #fff;
    font-size: 14px;
    font-style: normal;
    font-weight: 500;
    line-height: 45px;
    width: 100%;
    border: none;
    outline: none;
    box-shadow: none;
  }
}
@media screen and (max-width: 375px) {
  .modal-content {
    padding: 16px;
    margin-top: 0;
    height: calc(100vh - 30px);
  }
  .calendar-days {
    display: grid;
    grid-template-columns: repeat(7, 30px);
    text-align: center;
    grid-auto-rows: minmax(30px, 1fr);
    color: var(--White-Primary, #fff);
    font-size: 12px;
    font-weight: 400;
    line-height: 18px;
    gap: 5px;
  }
  .mobile-cal-piker {
    padding: 0 8px;
    gap: 6px;
  }
  .mobile-cal-piker input {
    font-size: 12px;
    line-height: 38px;
  }
  .custom-ele-box {
    padding: 12px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
  }
}

