.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: flex-end;
}

.modal-content {
  border-radius: 20px 20px 0px 0px;
  border: 1px solid #4d585d;
  background: #18242a;
  box-shadow: -1px 4px 34px 9px rgba(0, 0, 0, 0.65);
  max-width: 100%;
  padding: 20px;
  position: relative;
  height: calc(100% - 220px);
  width: 100%;
}
.modal-wrapper {
  display: flex;
  flex-direction: column;
  flex: 1;
  height: 100%;
  max-width: 920px;
  margin: 0 auto;
}
.modal-wrapper .modal-body {
  flex: 1;
  overflow: auto;
}
.modal-wrapper .footer-action {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 0;
  padding: 0 0 48px;
}
.footer-action .btn-cancel {
  border-radius: 10px;
  background: #2c3538;
  color: #ffc600;
  font-size: 16px;
  font-style: normal;
  font-weight: 600;
  width: 164px;
  line-height: 24px;
  text-transform: uppercase;
}
.footer-action .btn-cancel:hover {
  background: #ffc600;
  color: #2c3538;
}
.footer-action .btn-save {
  border-radius: 10px;
  background: #ffc600;
  color: #18242a;
  font-size: 16px;
  font-weight: 600;
  line-height: 24px;
  width: 164px;
  text-transform: uppercase;
}
.footer-action .btn-save:hover {
  background: #dfaf04;
  color: #18242a;
}
.modal-content .head-tag h1 {
  color: #fff;
  font-size: 20px;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 10px;
  line-height: normal;
}

.modal-content label {
  display: block;
  margin-top: 10px;
}
.form-main-wrapper {
  display: flex;
  align-items: center;
  gap: 20px;
  margin-top: 60px;
}
.modal-body .input-wrapper {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  flex: 1;
}
.modal-body .input-wrapper label {
  color: #fff;
  font-size: 12px;
  font-style: normal;
  font-weight: 500;
  line-height: 16px;
  letter-spacing: -0.36px;
  margin-bottom: 10px;
  margin-top: 0;
}
.back-arrow {
  width: 24px;
  height: 24px;
  min-width: 24px;
  border-radius: 50%;
  background: #2c3538;
  display: flex;
  align-items: center;
  justify-content: center;
}
.modal-body .input-wrapper input {
  border-radius: 10px;
  border: 1px solid #4d585d;
  background: #2c3538;
  outline: none;
}
.modal-body .input-wrapper input::placeholder {
  color: #4d585d;
  font-size: 16px;
  font-weight: 400;
  line-height: normal;
}
.modal-body .input-wrapper select {
  outline: none;
  color: #4d585d;
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: normal;
  border-radius: 10px;
  border: 1px solid #4d585d;
  background: #2c3538;
}

.modal-content select,
.modal-content input {
  height: 48px;
  width: 100%;
  padding: 10px;
  color: #fff;
  border: 1px solid #ccc;
  margin: 0;
  border-radius: 3px;
}

.modal-content button {
  padding: 10px 20px;
  background: #007bff;
  color: #fff;
  border: none;
  border-radius: 3px;
  cursor: pointer;
  margin-right: 10px;
}

.modal-content button:disabled {
  background: #ccc;
  cursor: not-allowed;
}

/* withdraw confirmation css */

.with-confirmation-wrapper {
  display: flex;
  align-items: flex-start;
  gap: 20px;
  flex: none !important;
  margin-top: 60px;
}
.with-confirmation-wrapper .form-main-wrapper {
  margin-top: 0;
}
.with-confirmation-wrapper .form-body {
  padding: 18px 40px;
  display: inline-flex;
  align-items: flex-start;
  border-radius: 10px;
  border: 1px solid #4d585d;
  background: #2c3538;
  flex-direction: column;
  gap: 20px;
}

.form-body .wrapper-list-item p {
  color: #fff;
  font-size: 14px;
  font-weight: 500;
  line-height: 20px;
  margin: 0;
}
.form-body .list-item-content h6,
.form-body .wrapper-list-item h6 {
  color: #d9d9d9;
  font-size: 12px;
  font-weight: 400;
  line-height: 16px;
  margin: 0;
}
.form-body .list-item-content p {
  color: #fff;
  font-size: 18px;
  font-weight: 700;
  line-height: 36px;
  margin: 0;
}
.form-body .wrapper {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  width: 100%;
  gap: 29px;
}
.with-confirmation-wrapper .footer-action {
  flex-direction: column;
  gap: 30px;
  padding: 0;
  align-items: center;
  display: flex;
  justify-content: center;
  height: 100%;
}
.payment-account {
  border-radius: 10px;
  background: #2c3538;
  border: 1px solid transparent;
  padding: 12px 40px 12px 12px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: all 0.5s ease;
}
:has(.container input:checked ~ .checkmark) .payment-account.active {
  border-radius: 10px;
  background: #2c3538;
  border: 1px solid #ffc600;
  padding: 25px 40px 25px 12px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: all 0.5s ease;
}
.payment-account .input-form {
  display: flex;
  gap: 12px;
}
.payment-account .input-form h5 {
  color: #fff;
  font-size: 14px;
  font-weight: 500;
  line-height: 20px;
  margin: 0;
}
.payment-acc-wrap {
  display: flex;
  flex-direction: column;
  gap: 24px;
}
.payment-account p {
  color: #868686;
  font-size: 12px;
  font-weight: 400;
  line-height: 16px;
  margin: 0;
}
ul {
  margin: 0;
  padding: 0;
}

ul li.checked {
  background: #888;
  color: #fff;
  text-decoration: line-through;
}

ul li.checked::before {
  content: "";
  position: absolute;
  border-color: #fff;
  border-style: solid;
  border-width: 0 2px 2px 0;
  top: 10px;
  left: 16px;
  transform: rotate(45deg);
  height: 15px;
  width: 7px;
}

.container {
  display: block;
  position: relative;
  padding-left: 20px;
  cursor: pointer;
  font-size: 22px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
}

.container input {
  position: absolute;
  opacity: 0;
  cursor: pointer;
}

/* Create a custom checkbox */
.checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: 20px;
  width: 20px;
  border: 1px solid #868686;
  border-radius: 50%;
}

.container:hover input ~ .checkmark {
  background-color: #18242a;
}

.container input:checked ~ .checkmark {
  background-color: #ffc600;
}

.checkmark:after {
  content: "";
  position: absolute;
  display: none;
}

.container input:checked ~ .checkmark:after {
  display: block;
}

.container .checkmark:after {
  left: 6.5px;
  top: 2px;
  width: 5px;
  height: 10px;
  border: solid #18242a;
  border-width: 0 3px 3px 0;
  transform: rotate(45deg);
}
@media screen and (max-width: 1024px) {
  .tag-number-wrap {
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    gap: 39px;
  }
  .modal-content {
    padding: 30px 50px;
  }
  .form-main-wrapper {
    flex-direction: column;
    padding: 0 16px;
    margin-top: 46px;
  }

  .modal-content select,
  .modal-content input {
    height: 44px;
  }
  .form-main-wrapper .input-wrapper {
    width: 100%;
  }
  .footer-action button.btn-save {
    background: #ffc600;
  }
  .footer-action button {
    margin-right: 0;
  }
  .footer-action button.btn-save:hover {
    opacity: 1;
  }
  .modal-wrapper .footer-action {
    gap: 30px;
  }
  .modal-wrapper .footer-action button {
    flex: 1;
    width: auto;
  }
  .with-confirmation-wrapper {
    flex-direction: column;
    flex: 1 !important;
  }
  .with-confirmation-wrapper .footer-action {
    flex-direction: row;
    width: 100%;
    height: auto;
  }
  .with-confirmation-wrapper .form-main-wrapper {
    height: 100%;
    width: 100%;
  }
  .with-confirmation-wrapper .form-body {
    width: 100%;
  }
  .form-body .wrapper {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    width: 100%;
    gap: 29px;
  }
}
@media screen and (max-width: 767px) {
  .tag-number-wrap {
    gap: 16px;
  }
  .modal-content {
    margin-top: 0;
    padding: 16px;
    height: calc(100% - 120px);
  }
  .with-confirm-modal {
    height: auto !important;
  }
  .with-confirm-modal .with-confirmation-wrapper {
    margin-top: 16px;
  }
  .form-main-wrapper {
    padding: 0;
    margin-top: 30px;
  }
  .modal-wrapper .footer-action {
    padding-bottom: 8px;
  }
  .form-body .wrapper {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    width: 100%;
    gap: 15px;
  }

  .with-confirmation-wrapper .form-body {
    padding: 16px;
  }
}
@media (max-width: 374px) {
  .tag-number-wrap {
    gap: 8px;
  }
}

