  { path: 'sports-book', component: SportsComponent },
  { path: 'live-sports', component: LiveSportsComponent },
  { path: 'virtual-sports', component: VirtualComponent },
  { path: 'jackpot', component: JackpotComponent },
  { path: 'promotion', component: PromotionsComponent },
  { path: 'blog', component: BlogComponent },
  { path: 'help-page', component: HelpComponent },
  { path: 'login-account', component: LoginComponent },
  { path: "register-account", component: RegistrationComponent },
  { path: "forgetpassword", component: ForgetComponent },
  { path: 'forget-success', component: ForgetSuccessComponent },
  { path: 'about-us', component: AboutComponent },
  { path: 'work-with-us', component: WorkWithUsComponent },
  { path: 'privacy-policy', component: PrivacyPolicyComponent },
  { path: 'terms-and-conditions', component: TNCComponent },
  { path: 'sports-betting-rules', component: SportsBettingRuleComponent },
  { path: 'responsible-gaming', component: ResponsibleGamingComponent },
  { path: 'faq', component: FAQComponent },
  { path: 'contact-us', component: ContactUsComponent },
  { path: 'blog', component: BlogComponent},
  { path: 'blog-content/:title', component: BlogPostComponent },
  { path: 'promotion-content/:title', component: PromoPostComponent },
  { path: 'edit-profile', component: MyProfileComponent},
  { path: 'edit-profile-profileupdate',component:ProfileUpdateComponent}

];

@NgModule({
  imports: [RouterModule.forRoot(routes, { useHash: true }),
  ],
  exports: [RouterModule],
})
export class AppRoutingModule { }

